    }
}

package advisor.manager;

import advisor.command.Command;
import advisor.command.CommandFactory;
import advisor.exception.InvalidInputException;
import advisor.service.SpotifyService;

import java.util.Scanner;

public class CommandManager {

    CommandFactory commandFactory;
    Scanner scanner;
    boolean getNextLine = true;

    boolean userLogged = false;

    public CommandManager(Scanner scanner) {
        this.scanner = scanner;
        this.commandFactory = new CommandFactory();
    }

    public void executeLine()
    {
        String nextLine = getNextLine();
        if (!nextLine.isEmpty()) {

            try {
                startCommand(nextLine);
            } catch (Exception e) {
                System.out.println(e.getMessage());

            }
        }

        if (getNextLine) {
            executeLine();
        }
    }

    private void startCommand(String line) throws InvalidInputException {
        Command command = commandFactory.getCommand(line);
        if (!command.isLoginRequired() || (command.isLoginRequired() && SpotifyService.isUserLogged())) {
            command.handle();
        }
        else {
            System.out.println("Please, provide access for application.");
        }

        if (command.isExitCommand()) {
            getNextLine = false;
        }
    }

    private String getNextLine() {
        return scanner.nextLine();
    }

}

package advisor.command;

public class HelpCommand extends Command{

//    static {
//        name = "/help";
//    }
//    protected static String name = "/help";

    private String helpMessage = "The program calculates the sum of numbers //n - calculator support the addition + and subtraction - operators";

    @Override
    public void handle() {
        System.out.println(helpMessage);
    }
}

package advisor.command;

public abstract class Command {

    protected boolean exitCommand = false;

    protected boolean loginRequired = true;

    public abstract void handle();

    public boolean isLoginRequired() {
        return loginRequired;
    }

    public boolean isExitCommand() {
        return exitCommand;
    }
}

package advisor.command;

public class ExitCommand extends Command{

    public ExitCommand() {
        this.exitCommand = true;
    }

    @Override
    public void handle() {
        System.out.println("---GOODBYE!---");
    }
}

package advisor.command;

import advisor.exception.InvalidInputException;

public class CommandFactory {

    private final String unknownMessage = "Unknown command";
    private final String missingParameterMessage = "Missing Parameter";

    private final String playlistsCommand = "playlists";
    private final String newCommand = "new";
    private final String featuredCommand = "featured";
    private final String categoriesCommand = "categories";
    private final String helpCommand = "help";
    private final String stopCommand = "exit";
    private final String authCommand = "auth";

    public Command getCommand(String commandLine) throws InvalidInputException {

        String[] parts = commandLine.split("\\s", 2);
        String textCommand = parts[0];

        String parameters = null;
        if (parts.length > 1 ) {
            parameters = parts[1];
        }

        Command command;
        switch (textCommand) {
            case helpCommand:
                command = new HelpCommand();
                break;
            case stopCommand:
                command = new ExitCommand();
                break;
            case featuredCommand:
                command = new FeaturedCommand();
                break;
            case newCommand:
                command = new NewCommand();
                break;
            case categoriesCommand:
                command = new CategoriesCommand();
                break;
            case authCommand:
                command = new AuthCommand();
                break;
            case playlistsCommand:
                if (parameters == null) {
                    throw new InvalidInputException(missingParameterMessage);
                }
                command = new PlaylistsCommand(parameters);
                break;
            default:
                throw new InvalidInputException(unknownMessage + " " + textCommand);
        }


        return command;
    }
}

package advisor.exception;

public class InvalidInputException extends Exception {

    public InvalidInputException(String errorMessage) {
        super(errorMessage);
    }
}

