package advisor.command;

public class PlaylistsCommand extends Command{

//    static {
//        name = "/help";
//    }
//    protected static String name = "/help";

    private String categoryName;

    private String title = "---C_NAME PLAYLISTS---";
    private String message =  "Mountains [Sia, Diplo, Labrinth]\n" +
            "Runaway [Lil Peep]\n" +
            "The Greatest Show [Panic! At The Disco]\n" +
            "All Out Life [Slipknot]";


    public PlaylistsCommand(String parametersString) {
        this.categoryName = parametersString;
        title = title.replaceAll("C_NAME", this.categoryName.toUpperCase());
    }

    @Override
    public void handle() {

        System.out.println(title);
        System.out.println(message);
    }
}

package advisor.command;

import advisor.service.SpotifyService;

public class AuthCommand extends Command{

//    protected static String name = "/help";

    private SpotifyService spotifyService;

    private String title = "";
    private String message =  "";

    public AuthCommand() {
        this.loginRequired = false;
        spotifyService = new SpotifyService();
    }

    @Override
    public void handle() {

        System.out.println(spotifyService.getAuthorizationUrl());
        System.out.println("---SUCCESS---");
    }

}

package advisor.service;

public class SpotifyService {

    private static String redirect_uri = "https://www.example.com&response_type=code";
    private static String uri = "https://accounts.spotify.com/authorize?";
    private static String client_id = "3c1f0675fb224be6807fdd578f1d84cb";
    private static String client_secret = "97cdf492b8934099a263ddd75f02d3ca";


    private static boolean userLogged = false;

    public static boolean isUserLogged() {
        return userLogged;
    }

    public String getAuthorizationUrl() {

        StringBuilder authUrl = new StringBuilder();
        authUrl.append(uri)
                .append("client_id=").append(client_id)
                .append("&").append("redirect_uri=").append(redirect_uri);

        //for now change flag right after gen auth link
        userLogged = true;

        return authUrl.toString();
    }
}

