if (!reply.contains("---PAGE 1 of 2---")) { return new CheckResult(false, "Something wrong with pagination format"); }
if (!reply.contains("---PAGE 2 of 2---")) { return new CheckResult(false, "Something wrong with pagination format"); }
if (!reply.contains(album1) || !reply.contains(album2)) { return new CheckResult(false, "Album from page 2 appeared on page 1 on "new" action"); }
if (countAppearances(reply, album1) > 1 || countAppearances(reply, album2) > 1) {
